import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import styled, {css}from 'styled-components'


const Title = styled.h1`
  font-size: 1.7em;
  text-align: center;
  color: palevioletred;
`;

const Button = styled.button`
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
`;

const Input = styled.input.attrs(({ size }) => ({
  margin: size || "1em",
  padding: size || "1em"
}))`
  color:black ;
  font-size: 1em;
  border: 2px solid orange;
  border-radius: 3px;
`;

class Game extends React.Component {
  constructor() {
    super();
    this.state = {
        now: new Date(),
        fecha_final: new Date(),
        days:'',
        weekend:'',
        yearc:'',
        yeara:'',
        daysw: '',
      }
        this.handleDia = this.handleDia.bind(this);
        // this.handleDiaAño = this.handleDiaAño.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
}

handleDia(e)
 {
    e.preventDefault();
    var result = new Date();
    console.log("result--->",result.getDay());
    var days = ["Domingo", "Lunes", "Martes", "Miercoles", "Jueves", "Viernes", "Sabado"];
    var daysw = days[result.getDay()];
    console.log("el dia es:--->",daysw);

    var now = new Date();
    var start = new Date(now.getFullYear(), 0, 0);
    var diff = now - start;
    // console.log("diff-->",diff);
    var oneDay = 1000 * 60 * 60 * 24;
    // console.log("oneDay-->",oneDay);
    var day = Math.floor(diff / oneDay);
    var weekend = (day /7 )+1;
    var fecha_final = new Date('2019-5-23');
    var resultado = now - fecha_final;
    var yearc = (resultado / oneDay);
    var yeara = (day-365)
    console.log("faltan de cumpleaños-->",yearc);
    console.log("faltan para el año-->",yeara);
    console.log("numero de la semana-->",weekend);
    console.log('dia del año: ' + day);


    // var d = new Date(Date.UTC(d.getFullYear(), d.getMonth(), d.getDate()));
    // d.setUTCDate(d.getUTCDate() + 4 - (d.getUTCDay()||7));
    // var yearStart = new Date(Date.UTC(d.getUTCFullYear(),0,1));
    // var weekNo = Math.ceil(( ( (d - yearStart) / 86400000) + 1)/7);
    // // var result = getWeekNumber(new Date());
    // console.log ("La semana es:-->",weekNo);
}



  // handleDiaAño({month,day}) {
  //     var result2 = new Date();
  //     day + dias anteriores
  //     return result2;
  //     console.log();
  //   }
  //
  //   handleMenosDiaAño({month,day}) {
  //       var result2 = new Date();
  //       day - dias cumpleaños
  //       return result2;
  //       console.log();
  //     }


//   handleSubmit(e) {
//    e.preventDefault();
//    var resultadofinal = this.handleComparativa(this.state)
//    this.setState({
//      result: resultadofinal,
//    });
// }
handleInputChange(e) {
    const {value, name} = e.target;
    // console.log(value, name);
    this.setState({
      [name]: value
    });
}

      //console.log("el numero de en medio es--->",result3);
  render() {

    return (

    <div>
      <form onSubmit={this.handleDia}>
      <Title>
      <h1>FECHAS</h1>
      </Title>

      <h4> Fecha </h4>
      <Input
        type="date"
        name="fecha"
        value={this.state.getDate}
        onChange={this.handleInputChange}
        placeholder="Ingrese fecha"
        />


      <h4> Dia </h4>
      <Input
        type="text"
        name="dia"
        value={this.state.day}
        onChange={this.handleInputChange}
        />


      <h4> Dia del año </h4>
      <Input
      type="text"
      name="año"
      value={this.state.daysw}
      onChange={this.handleInputChange}
      />

      <h4> Semana del año </h4>
      <Input
      type="text"
      name="semana"
      value={this.state.weekend}
      onChange={this.handleInputChange}
      />

      <h4> Ingresa tu fecha de cumpleaños </h4>
      <Input
      type="date"
      name="fcumple"
      value={this.state.fecha_final}
      />

      <h4> ¿Cuantos dias faltan para tu cumpleaños? </h4>
      <Input
      type="text"
      name="cumpleaños"
      value={this.state.yearc}
      onChange={this.handleInputChange}
      />

      <h4> ¿Cuantos faltan para terminar el año? </h4>
      <Input
      type="text"
      name="año"
      value={this.state.yeara}
      onChange={this.handleInputChange}
      />

      <Button
      type="submit">
      Resultado
      </Button>
      </form>
    </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
