  render() {

    return (

    <div>
      <form onSubmit={this.handleDia}>
      <Title>
      <h1>Valor Medio</h1>
      </Title>
      <h4> Primer valor </h4>
      <Input
        type="text"
        name="num1"
        value={this.state.num1}
        onChange={this.handleInputChange}
        placeholder="Ingrese numero"
        />

      <h4> Segundo valor </h4>
      <Input
        type="text"
        name="num2"
        value={this.state.num2}
        onChange={this.handleInputChange}
        placeholder="Ingrese  numero"
        />

      <h4> Tercer valor </h4>
      <Input
      type="text"
      name="num3"
      value={this.state.num3}
      onChange={this.handleInputChange}
      placeholder="Ingrese numero"
      />

      <Button
      type="submit">
      Comparar
      </Button>

      <h4> Resultado </h4>
      <Input
        type="text"
        name="result"
        value={this.state.result}

        />
      </form>
    </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
