import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import styled, { css } from 'styled-components'

const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;


  ${props =>
      props.primary &&  css`
        background: palevioletred;
        color: white;
        padding: 0em 1em;
        margin: 0.25 1em;
        border-radius: 3px;
        border: 2px solid white;

      `};
`;
const Title = styled.h1`
  font-size: 2.5em;
  text-align: center;
  color: green;
`;

const Wrapper = styled.section`
  padding: 5em;
  background: white;
`;

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component {
  renderSquare(i) {
    return (
      <Square
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }

  render() {
    return (
      <Wrapper>
      <Title>
      Juego de Gato
      </Title>

      <div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
      </Wrapper>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);

  }


  render() {


    return (
      <div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(<Game />, document.getElementById("root"));
